CREATE TABLE store_extra_data (id BIGINT AUTO_INCREMENT, store_id BIGINT, info TEXT, INDEX store_id_idx (store_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE stores (id BIGINT AUTO_INCREMENT, brand VARCHAR(20) DEFAULT NULL, type VARCHAR(20) DEFAULT NULL, model VARCHAR(3) DEFAULT NULL, status VARCHAR(20) DEFAULT NULL, area VARCHAR(50) DEFAULT NULL, country VARCHAR(50) DEFAULT NULL, city VARCHAR(50) DEFAULT NULL, name VARCHAR(255), address VARCHAR(255), zip VARCHAR(20), telf VARCHAR(20), last_update VARCHAR(30), product_line VARCHAR(30), latitude VARCHAR(10) DEFAULT '0', longitude VARCHAR(10) DEFAULT '0', INDEX brand_idx_idx (brand), INDEX type_idx_idx (type), INDEX model_idx_idx (model), INDEX status_idx_idx (status), INDEX area_idx_idx (area), INDEX country_idx_idx (country), INDEX city_idx_idx (city), INDEX name_idx_idx (name), PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE store_extra_data ADD CONSTRAINT store_extra_data_store_id_stores_id FOREIGN KEY (store_id) REFERENCES stores(id) ON DELETE CASCADE;
